mapper.update:
update student
sno=#{student.sno}, name=#{student.name}, sex=#{student.sex}, birthday=#{student.birthday}, nation=#{student.nation}, tel=#{student.tel}, email=#{student.email}, address=#{student.address}, password=#{student.password}, classid=#{student.classid}, powerid=#{student.powerid}, des=#{student.des},
where id=#{student.id}
